package day13whiledowhileloops;

public class WhileLoop02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// ilk 5 sayma sayisinin  carpimini  ekrana yazdirin
		
		
		
		int product=1;
		
		int num=1;
		
		while (num<6) {
			
			product=product*num;
			System.out.println(product);
			num++;
			
			
		}
		
		
		
	}

}
