package day13whiledowhileloops;

public class WhileLoop03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// 3un carpim tablosunu  ekrana  yazdiran bir program yazdiriin
		
		//3*1=3,3*2=6,.......3*10=30
		
		
		//  i degisimi temsil ediyor   
		
		for (int i=1; i<11; i++) {
			
			
			System.out.println("3x"+i+"="+(3*i));
			
		}
		
		System.out.println("While ile cozum ");
		int num=1;
		while (num<11 ) {
			
		System.out.println("3x"+num+"="+3*num);	// 3x  bir string resim 
		}
		
		
	}

}
